// Konvertiere Binary zu Base64 für Vertex AI
const binary = $binary?.data;
const binaryData = binary?.data || binary;

if (!binaryData) {
  throw new Error('Kein Binary-Daten gefunden!');
}

// Base64 Encoding
const base64 = Buffer.from(binaryData).toString('base64');
const mimeType = binary?.mimeType || 'image/jpeg';

return [{
  json: {
    sample: $json.sample || '',
    source: $json.source || 'Upload',
    imageBase64: base64,
    mimeType: mimeType
  }
}];

