// Parse Vertex AI Gemini Response
const response = $json;
const source = $('Setze Sample-Info').item.json.source || 'Upload';
const sample = $('Setze Sample-Info').item.json.sample || '';

// Vertex AI Response Struktur:
// {
//   "candidates": [{
//     "content": {
//       "parts": [{
//         "text": "{\"name\": \"...\"}"
//       }]
//     }
//   }]
// }

let extractedData = {};

try {
  // Versuche JSON aus Response zu extrahieren
  const text = response.candidates?.[0]?.content?.parts?.[0]?.text || response.text || '';

  // Extrahiere JSON aus Text (falls zusätzlicher Text vorhanden)
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  const jsonString = jsonMatch ? jsonMatch[0] : text;

  // Parse JSON
  extractedData = JSON.parse(jsonString);
} catch (error) {
  // Fallback: Versuche direkt zu parsen
  if (typeof response === 'object') {
    extractedData = response;
  } else {
    console.error('Fehler beim Parsen der Vertex AI Response:', error);
    extractedData = {};
  }
}

// Füge Metadaten hinzu
return [{
  json: {
    name: extractedData.name || '',
    company: extractedData.company || '',
    email: extractedData.email || '',
    phone: extractedData.phone || extractedData.phone_mobile || '',
    phone_office: extractedData.phone_office || extractedData.phone_büro || '',
    address: extractedData.address || '',
    city: extractedData.city || '',
    website: extractedData.website || extractedData.www || '',
    job_title: extractedData.job_title || extractedData.position || '',
    source: source,
    sample: sample,
    timestamp: new Date().toISOString()
  }
}];

// Formatiere Daten für die Website
const data = $json;
return [{
  json: {
    type: 'business-card-processed',
    payload: {
      name: data.name || '',
      company: data.company || '',
      email: data.email || '',
      phone: data.phone || '',
      phone_office: data.phone_office || '',
      address: data.address || '',
      city: data.city || '',
      website: data.website || '',
      job_title: data.job_title || '',
      source: data.source || 'Upload',
      sample: data.sample || '',
      timestamp: data.timestamp || new Date().toISOString()
    }
  }
}];
